import React, { useState, useRef } from 'react';
import ColorThief from 'color-thief-browser';

const App = () => {
  const [imagePreview, setImagePreview] = useState(null);
  const [palette, setPalette] = useState([]);
  const imageRef = useRef();

  const handleImageUpload = (e) => {
    const file = e.target.files[0];
    if (!file) return;

    const reader = new FileReader();
    reader.onload = (event) => {
      setImagePreview(event.target.result);
    };
    reader.readAsDataURL(file);
  };

  const extractColors = async () => {
    try {
      const thief = new ColorThief();
      const colors = await thief.getPalette(imageRef.current, 6); // top 6 colors
      const hexColors = colors.map(([r, g, b]) => `#${((1 << 24) + (r << 16) + (g << 8) + b).toString(16).slice(1)}`);
      setPalette(hexColors);
    } catch (error) {
      console.error("Color extraction failed:", error);
    }
  };

  return (
    <div className="min-h-screen bg-blue-900 text-white flex flex-col items-center justify-start p-6 font-chillax">
      <h1 className="text-3xl font-bold mb-6">üñºÔ∏è Image Color Palette Generator</h1>

      <input
        type="file"
        accept="image/*"
        onChange={handleImageUpload}
        className="mb-4 text-white"
      />

      {imagePreview && (
        <div className="mb-6">
          <img
            ref={imageRef}
            src={imagePreview}
            alt="Uploaded"
            crossOrigin="anonymous"
            className="w-64 h-64 object-cover rounded-xl shadow-lg border border-white/30"
          />
          <button
            onClick={extractColors}
            className="mt-4 bg-white text-blue-900 px-6 py-2 rounded-lg hover:bg-gray-200 transition font-semibold"
          >
            Extract Colors üé®
          </button>
        </div>
      )}

      {palette.length > 0 && (
        <div className="backdrop-blur-md bg-white/10 border border-white/30 shadow-xl rounded-2xl p-8 w-full max-w-4xl mt-6">
          <h2 className="text-xl font-semibold mb-4">Extracted Palette:</h2>
          <div className="grid grid-cols-2 md:grid-cols-6 gap-4">
            {palette.map((color, idx) => (
              <div key={idx} className="flex flex-col items-center">
                <div
                  className="w-20 h-20 rounded-lg shadow-inner border border-white/20"
                  style={{ backgroundColor: color }}
                />
                <p className="mt-2 text-sm">{color}</p>
              </div>
            ))}
          </div>
        </div>
      )}
    </div>
  );
};

export default App;
