import React, { useState } from 'react';
import ColorBox from './ColorBox';

const generateRandomColor = () => {
  const hex = Math.floor(Math.random() * 0xffffff).toString(16);
  return `#${hex.padStart(6, '0')}`;
};

const App = () => {
  const [colors, setColors] = useState(
    Array.from({ length: 4 }, generateRandomColor)
  );
  const [searchColor, setSearchColor] = useState('');
  const [searchedColor, setSearchedColor] = useState('');

  const generatePalette = () => {
    const newColors = Array.from({ length: 4 }, generateRandomColor);
    setColors(newColors);
    setSearchedColor(''); // reset searched color on new palette
  };

  const handleSearch = () => {
    if (searchColor.trim()) {
      setSearchedColor(searchColor.trim());
    }
  };

  return (
    <div className="min-h-screen bg-blue-900 flex flex-col items-center justify-center p-6 font-chillax text-white">
      <h1 className="text-4xl font-bold mb-6 drop-shadow-lg">ðŸŽ¨ Random Color Palette Generator</h1>

      {/* SEARCH BAR */}
      <div className="flex gap-3 mb-8">
        <input
          type="text"
          placeholder="Enter color name or hex"
          value={searchColor}
          onChange={(e) => setSearchColor(e.target.value)}
          className="px-4 py-2 rounded-lg text-black outline-none w-64"
        />
        <button
          onClick={handleSearch}
          className="bg-white text-blue-900 px-4 py-2 rounded-lg font-semibold hover:bg-gray-100 transition"
        >
          Search Color
        </button>
      </div>

      {/* COLOR DISPLAY */}
      {searchedColor && (
        <div className="mb-6">
          <h2 className="text-xl font-semibold mb-2">Your Searched Color:</h2>
          <ColorBox color={searchedColor} />
        </div>
      )}

      {/* RANDOM COLOR PALETTE */}
      <div className="backdrop-blur-md bg-white/10 border border-white/20 shadow-xl rounded-2xl p-8 mb-8 w-full max-w-4xl">
        <div className="grid grid-cols-2 md:grid-cols-4 gap-6">
          {colors.map((color, idx) => (
            <ColorBox key={idx} color={color} />
          ))}
        </div>
      </div>

      <button
        onClick={generatePalette}
        className="bg-white text-blue-900 px-6 py-3 rounded-xl font-semibold hover:bg-gray-100 transition drop-shadow-md"
      >
        Generate New Palette
      </button>
    </div>
  );
};

export default App;
