import React, { useState } from 'react';
import ColorBox from './ColorBox';

const generateRandomColor = () => {
  const hex = Math.floor(Math.random() * 0xffffff).toString(16);
  return `#${hex.padStart(6, '0')}`;
};

const allColors = [
  "#FF0000", "#FF6347", "#FFA500", "#FFFF00", "#ADFF2F", "#00FF00", "#00CED1",
  "#1E90FF", "#0000FF", "#8A2BE2", "#FF69B4", "#A52A2A", "#708090", "#000000", "#FFFFFF"
];

const App = () => {
  const [colors, setColors] = useState(
    Array.from({ length: 4 }, generateRandomColor)
  );
  const [selectedColor, setSelectedColor] = useState(null);

  const generatePalette = () => {
    const newColors = Array.from({ length: 4 }, generateRandomColor);
    setColors(newColors);
  };

  return (
    <div className="min-h-screen bg-blue-900 flex flex-row items-start justify-center gap-8 p-6 font-chillax text-white relative">

      {/* LEFT: SCROLLABLE VERTICAL COLOR LIST */}
      <div className="h-[500px] w-24 overflow-y-scroll bg-white/10 backdrop-blur-md border border-white/30 rounded-2xl p-2 shadow-xl">
        {allColors.map((color, idx) => (
          <div
            key={idx}
            onClick={() => setSelectedColor(color)}
            className="cursor-pointer mb-2 hover:scale-105 transition"
            title={color}
          >
            <div
              className="h-10 w-full rounded-lg border border-white/20"
              style={{ backgroundColor: color }}
            />
            <p className="text-xs text-center mt-1">{color}</p>
          </div>
        ))}
      </div>

      {/* CENTER: MAIN COLOR PALLET */}
      <div className="flex flex-col items-center justify-center">
        <h1 className="text-4xl font-bold mb-6 drop-shadow-lg">ðŸŽ¨ Random Color Palette Generator</h1>
        <div className="grid grid-cols-2 md:grid-cols-4 gap-4 mb-6">
          {colors.map((color, idx) => (
            <ColorBox key={idx} color={color} />
          ))}
        </div>
        <button
          onClick={generatePalette}
          className="bg-white text-blue-900 px-6 py-3 rounded-xl font-semibold hover:bg-gray-100 transition drop-shadow-md"
        >
          Generate New Palette
        </button>
      </div>

      {/* OVERLAY FOR SELECTED COLOR */}
      {selectedColor && (
        <div
          className="fixed inset-0 bg-black/50 backdrop-blur-sm flex items-center justify-center z-50"
          onClick={() => setSelectedColor(null)}
        >
          <div className="bg-white/10 backdrop-blur-lg border border-white/30 p-10 rounded-2xl shadow-2xl text-center">
            <div
              className="w-64 h-64 rounded-xl mx-auto border border-white/20 shadow-inner"
              style={{ backgroundColor: selectedColor }}
            />
            <p className="text-xl mt-4 font-semibold">{selectedColor}</p>
            <p className="text-sm text-gray-200 italic mt-2">(Click anywhere to close)</p>
          </div>
        </div>
      )}
    </div>
  );
};

export default App;
