import React, { useState } from 'react';
import ColorBox from './ColorBox';

const predefinedShades = {
  yellow: [
    "#FFFFE0", "#FFFACD", "#FAFAD2", "#FFEFD5",
    "#FFE4B5", "#FFD700", "#FFFF00", "#F0E68C",
    "#FFDAB9", "#F5DEB3", "#FFECB3", "#FFF176"
  ],
  blue: [
    "#E3F2FD", "#BBDEFB", "#90CAF9", "#64B5F6",
    "#42A5F5", "#2196F3", "#1E88E5", "#1976D2",
    "#1565C0", "#0D47A1"
  ],
  red: [
    "#FFEBEE", "#FFCDD2", "#EF9A9A", "#E57373",
    "#EF5350", "#F44336", "#E53935", "#D32F2F",
    "#C62828", "#B71C1C"
  ],
  green: [
    "#E8F5E9", "#C8E6C9", "#A5D6A7", "#81C784",
    "#66BB6A", "#4CAF50", "#43A047", "#388E3C",
    "#2E7D32", "#1B5E20"
  ]
};

const App = () => {
  const [shades, setShades] = useState([]);
  const [searchColor, setSearchColor] = useState('');
  const [page, setPage] = useState(0);
  const colorsPerPage = 4;

  const handleSearch = () => {
    const color = searchColor.toLowerCase().trim();
    if (predefinedShades[color]) {
      setShades(predefinedShades[color]);
      setPage(0);
    } else {
      alert("Color not found. Try 'yellow', 'blue', 'red', or 'green'.");
    }
  };

  const handleNext = () => {
    if ((page + 1) * colorsPerPage < shades.length) {
      setPage(prev => prev + 1);
    }
  };

  const handlePrev = () => {
    if (page > 0) {
      setPage(prev => prev - 1);
    }
  };

  const visibleShades = shades.slice(page * colorsPerPage, (page + 1) * colorsPerPage);

  return (
    <div className="min-h-screen bg-blue-900 flex flex-col items-center justify-center p-6 font-chillax text-white">
      <h1 className="text-4xl font-bold mb-6 drop-shadow-lg">ðŸŽ¨ Color Shade Explorer</h1>

      {/* SEARCH BAR */}
      <div className="flex gap-3 mb-6">
        <input
          type="text"
          placeholder="Enter color (e.g. yellow)"
          value={searchColor}
          onChange={(e) => setSearchColor(e.target.value)}
          className="px-4 py-2 rounded-lg text-black outline-none w-64"
        />
        <button
          onClick={handleSearch}
          className="bg-white text-blue-900 px-4 py-2 rounded-lg font-semibold hover:bg-gray-100 transition"
        >
          Search Color
        </button>
      </div>

      {/* COLOR PALLET DISPLAY */}
      {visibleShades.length > 0 && (
        <>
          <div className="backdrop-blur-md bg-white/10 border border-white/20 shadow-xl rounded-2xl p-8 mb-4 w-full max-w-4xl">
            <div className="grid grid-cols-2 md:grid-cols-4 gap-6">
              {visibleShades.map((color, idx) => (
                <ColorBox key={idx} color={color} />
              ))}
            </div>
          </div>

          {/* PAGINATION BUTTONS */}
          <div className="flex gap-4 mb-8">
            <button
              onClick={handlePrev}
              disabled={page === 0}
              className={`px-4 py-2 rounded-lg ${
                page === 0 ? 'bg-gray-400 cursor-not-allowed' : 'bg-white text-blue-900 hover:bg-gray-100'
              } transition font-semibold`}
            >
              â—€ Prev
            </button>
            <button
              onClick={handleNext}
              disabled={(page + 1) * colorsPerPage >= shades.length}
              className={`px-4 py-2 rounded-lg ${
                (page + 1) * colorsPerPage >= shades.length ? 'bg-gray-400 cursor-not-allowed' : 'bg-white text-blue-900 hover:bg-gray-100'
              } transition font-semibold`}
            >
              Next â–¶
            </button>
          </div>
        </>
      )}
    </div>
  );
};

export default App;
